/* Styles go here */


* {
    color: black;
}

.print-format td, .print-format th{
    padding: 0 !important;
}

.print-heading {
    display: none;
}

.page-header,
.page-header-space {
    height: 140px;
}

.page-footer,
.page-footer-space {
    /*height: 120px;*/

}

.page-footer {
    position: fixed;
    bottom: 0;
    width: 100%;
    margin: auto;

}

.page-break-before {
    page-break-before: always;
}


.page-header {
    position: fixed;
    top: 0mm;
    display: block;
    width: 100%;
    border-bottom: 0 !important;
    color: #ffffff;
    margin-left: 0px !important;
    padding-left: 0 !important;
}

.print-format .print-format-preview {
    padding-left: 0px !important;
}

.scope-work {
    margin-inline: 60px;
    margin-bottom: 30px;

}

.scope-work-table {
    border: 1px solid black;
    color: black !important;

}

.scope-work-table {
    width: 100%;
    border-collapse: collapse;

}

.scope-work-table td {
    text-align: start;
    vertical-align: top;
}

.scope-work-table th {
    background-color: #c9daf8;
    color: black !important;
    text-align: center;
    font-weight: bold !important;

}

.scope-work-table th:nth-child(1) {
    width: 20%;
    min-width: 150px;
}

.scope-work-table th:nth-child(2) {
    width: 20%;
    min-width: 150px;
}

.scope-work-table th:nth-child(3) {
    width: 60%;
}

.test-table {
    color: black !important;
    width: 100%;
    border-collapse: collapse;

}

.test-table td {
    text-align: left;
    /*border-top: 1px solid black;*/
    border-bottom: 1px solid black;
    border-left: 1px solid black;
    border-right: 1px solid black;
}

.test-table td:nth-child(1) {
    width: 20%;
    min-width: 150px;
}

.test-table td:nth-child(2) {
    width: 20%;
    min-width: 150px;
}

.test-table td:nth-child(3) {
    width: 60%;
}



@page {
    size: A4 potrait;
    margin: 10mm;
    /*margin-bottom: 55px;*/

}

@media print {
    thead {
        display: table-header-group;
    }

    tfoot {
        display: table-footer-group;
    }

    /*.page-header,*/
    .page-header-space {
        /*height: 50px;*/
        border-bottom: 0 !important;
    }

    /*.page-header,*/
    .page-footer-space {
        height:-200px;

    }

    @media print {
        .page-break-before {
            page-break-before: always;
        }

        .page-header {
            display: block;
        }

        .print-format .print-format-preview {
            padding-left: 0px !important;
            padding-top: 0px !important;
        }

    }

    button {
        display: none;
    }

    body {
        margin: 0;
    }
}

.page-header {
    margin: 0px !important;
    padding: 0 !important;
}

.text-center h4 {
    display: none;
}

body {
        counter-reset: page;
    }


.page-no::after {
        counter-increment: page;
        content: "Page " counter(page);
    }

